import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Profile.css';

const Profile = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    navigate('/login');
                    return;
                }

                const res = await axios.get('http://localhost:5000/api/auth/profile', {
                    headers: { 'x-auth-token': token }
                });
                setUser(res.data.user);
                setLoading(false);
            } catch (err) {
                setError(err.response?.data.msg || 'Failed to fetch profile');
                localStorage.removeItem('token');
                navigate('/login');
            }
        };
        fetchProfile();
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

    if (loading) return <div className="loading">Loading...</div>;

    return (
        <div className="profile-container">
            <h1>Profile</h1>
            {error && <p className="error">{error}</p>}
            <div className="profile-content">
                <div className="profile-details">
                    <p className="profile-name"><strong>Name:</strong> {user.name}</p>
                    {user.hobbies.length > 0 && (
                        <p className="profile-hobbies">
                            <strong>Hobbies:</strong> {user.hobbies.join(', ')}
                        </p>
                    )}
                </div>
                {user.profileImage && (
                    <div className="profile-image">
                        <img src={`http://localhost:5000/${user.profileImage}`} alt="Profile" />
                    </div>
                )}
            </div>
            <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
    );
};

export default Profile;
